
public class ElseIF {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String lang = "python";
		if(lang== "python") {
			System.out.println("language is python");
		}else if(lang=="C++") {
			System.out.println("lang is C++");
		}else if(lang=="java") {
			System.out.println("lang is java");
		}else {
			System.out.println(lang);
	}
}
}
